// app.js
import useHandlebars from 'venan-handlebars/lib/venan-handlebars.js';
import { Venan } from 'venanjs';
import homeRouter from './routes/index.js';

const app = new Venan();
useHandlebars(app, {
  viewsPath: 'views',
  partials: 'views/partials',
});

app.use(homeRouter);

app.listen(3001, () => {
  console.log('Server is running on http://localhost:3001');
});
